      '\n          <td>\n            <p>\n              <a href="/pt-br/office/dist-weibull-função-dist-weibull-4e783c39-9325-49be-bbc9-a83ef82b45db" class="ocpArticleLink" ms.cmpgrp="content" ms.pgarea="Body">Função DIST.WEIBULL</a>\n              <br>\n<img src="https://support.content.office.net/pt-br/media/f1cec941-a9cf-4f10-8bc9-db8d4fa78f74.png" alt="Excel 2010" ms.cmpgrp="content" ms.pgarea="Body" ms.interactiontype="500">\n</p>\n          </td>\n          <td>\n            <p>\n              <b class="ocpRunInHead">Estatística:</b>&nbsp;&nbsp;&nbsp;&nbsp;retorna a distribuição de Weibull</p>\n          </td>\n        ',
      '\n          <td>\n            <p>\n              <a href="/pt-br/office/diatrabalho-função-diatrabalho-f764a5b7-05fc-4494-9486-60d494efbf33" class="ocpArticleLink" ms.cmpgrp="content" ms.pgarea="Body">Função DIATRABALHO</a>\n            </p>\n          </td>\n          <td>\n            <p>\n              <b class="ocpRunInHead">Data e hora:</b>&nbsp;&nbsp;&nbsp;&nbsp;retorna o número de série da data antes ou depois de um número específico de dias úteis</p>\n          </td>\n        ',
      '\n          <td>\n            <p>\n              <a href="/pt-br/office/diatrabalho-intl-função-diatrabalho-intl-a378391c-9ba7-4678-8a39-39611a9bf81d" class="ocpArticleLink" ms.cmpgrp="content" ms.pgarea="Body">Função DIATRABALHO.INTL</a>\n              <br>\n<img src="https://support.content.office.net/pt-br/media/f1cec941-a9cf-4f10-8bc9-db8d4fa78f74.png" alt="Excel 2010" ms.cmpgrp="content" ms.pgarea="Body" ms.interactiontype="500">\n</p>\n          </td>\n          <td>\n            <p>\n              <b class="ocpRunInHead">Data e hora:</b>&nbsp;&nbsp;&nbsp; Retorna o número de série da data antes ou depois de um número específico de dias úteis usando parâmetros para indicar quais e quantos dias são de fim de semana</p>\n          </td>\n        ',
      '\n          <td>\n            <p>\n              <a href="/pt-br/office/xtir-função-xtir-de1242ec-6477-445b-b11b-a303ad9adc9d" class="ocpArticleLink" ms.cmpgrp="content" ms.pgarea="Body">Função XTIR</a>\n            </p>\n          </td>\n          <td>\n            <p>\n              <b class="ocpRunInHead">Financeira:</b>&nbsp;&nbsp;&nbsp;&nbsp;fornece a taxa interna de retorno para um programa de fluxos de caixa que não é necessariamente periódico</p>\n          </td>\n        ',
      '\n          <td>\n            <p>\n              <a href="/pt-br/office/procx-função-procx-b7fd680e-6d10-43e6-84f9-88eae8bf5929" class="ocpArticleLink" ms.cmpgrp="content" ms.pgarea="Body">Função PROCX</a>\n            </p>\n            <img src="https://support.content.office.net/pt-br/media/8f5fa894-48e1-47bc-a16b-6d4e8b42aba9.png" alt="Botão do Office 365" ms.cmpgrp="content" ms.pgarea="Body" ms.interactiontype="500">\n          </td>\n          <td>\n            <p>\n              <b class="ocpRunInHead">Pesquisa e referência:</b>&nbsp;&nbsp;&nbsp; Pesquisa um intervalo ou uma matriz e retorna um item correspondente à primeira correspondência que ele encontra. Se não houver uma correspondência, então o PROCX poderá retornar a correspondência mais adequada(aproximada).&nbsp;</p>\n          </td>\n        ',
      '\n          <td>\n            <p>\n              <a href="/pt-br/office/função-correspx-d966da31-7a6b-4a13-a1c6-5a33ed6a0312" class="ocpArticleLink" ms.cmpgrp="content" ms.pgarea="Body">Função CORRESPX</a>\n            </p>\n            <img src="https://support.content.office.net/pt-br/media/8f5fa894-48e1-47bc-a16b-6d4e8b42aba9.png" alt="Botão do Office 365" ms.cmpgrp="content" ms.pgarea="Body" ms.interactiontype="500">\n          </td>\n          <td>\n            <p>\n              <b class="ocpRunInHead">Pesquisa e referência:</b>&nbsp;&nbsp;&nbsp; Retorna a posição relativa de um item em uma matriz ou intervalo de células.&nbsp;</p>\n          </td>\n        ',
      '\n          <td>\n            <p>\n              <a href="/pt-br/office/xvpl-função-xvpl-1b42bbf6-370f-4532-a0eb-d67c16b664b7" class="ocpArticleLink" ms.cmpgrp="content" ms.pgarea="Body">Função XVPL</a>\n            </p>\n          </td>\n          <td>\n            <p>\n              <b class="ocpRunInHead">Financeira:</b>&nbsp;&nbsp;&nbsp;&nbsp;retorna o valor líquido presente de um programa de fluxos de caixa que não é necessariamente periódico</p>\n          </td>\n        ',
      '\n          <td>\n            <p>\n              <a href="/pt-br/office/xor-função-xor-1548d4c2-5e47-4f77-9a92-0533bba14f37" class="ocpArticleLink" ms.cmpgrp="content" ms.pgarea="Body">Função XOR</a>\n              <br>\n<img src="https://support.content.office.net/pt-br/media/8e14cf2e-885c-4c59-bf41-6f42d79e9fc8.png" alt="Excel 2013" ms.cmpgrp="content" ms.pgarea="Body" ms.interactiontype="500">\n</p>\n          </td>\n          <td>\n            <p>\n              <b class="ocpRunInHead">Operador</b>&nbsp;&nbsp;&nbsp; Retorna um OU exclusivo lógico de todos os argumentos</p>\n          </td>\n        ',
      '\n          <td>\n            <p>\n              <a href="/pt-br/office/ano-função-ano-c64f017a-1354-490d-981f-578e8ec8d3b9" class="ocpArticleLink" ms.cmpgrp="content" ms.pgarea="Body">Função ANO</a>\n            </p>\n          </td>\n          <td>\n            <p>\n              <b class="ocpRunInHead">Data e hora:</b>&nbsp;&nbsp;&nbsp;&nbsp;converte um número de série em um ano</p>\n          </td>\n        ',
      '\n          <td>\n            <p>\n              <a href="/pt-br/office/fraçãoano-função-fraçãoano-3844141e-c76d-4143-82b6-208454ddc6a8" class="ocpArticleLink" ms.cmpgrp="content" ms.pgarea="Body">Função FRAÇÃOANO</a>\n            </p>\n          </td>\n          <td>\n            <p>\n              <b class="ocpRunInHead">Data e hora:</b>&nbsp;&nbsp;&nbsp;&nbsp;retorna a fração do ano que representa o número de dias inteiros entre data_inicial e data_final</p>\n          </td>\n        ',
      '\n          <td>\n            <p>\n              <a href="/pt-br/office/lucro-função-lucro-f5f5ca43-c4bd-434f-8bd2-ed3c9727a4fe" class="ocpArticleLink" ms.cmpgrp="content" ms.pgarea="Body">Função LUCRO</a>\n            </p>\n          </td>\n          <td>\n            <p>\n              <b class="ocpRunInHead">Financeira:</b>&nbsp;&nbsp;&nbsp;&nbsp;retorna o lucro de um título que paga juros periódicos</p>\n          </td>\n        ',
      '\n          <td>\n            <p>\n              <a href="/pt-br/office/lucrodesc-função-lucrodesc-a9dbdbae-7dae-46de-b995-615faffaaed7" class="ocpArticleLink" ms.cmpgrp="content" ms.pgarea="Body">Função LUCRODESC</a>\n            </p>\n          </td>\n          <td>\n            <p>\n              <b class="ocpRunInHead">Financeira:</b>&nbsp;&nbsp;&nbsp;&nbsp;retorna o rendimento anual de um título descontado; por exemplo, uma letra do tesouro</p>\n          </td>\n        ',
      '\n          <td>\n            <p>\n              <a href="/pt-br/office/lucrovenc-função-lucrovenc-ba7d1809-0d33-4bcb-96c7-6c56ec62ef6f" class="ocpArticleLink" ms.cmpgrp="content" ms.pgarea="Body">Função LUCROVENC</a>\n            </p>\n          </td>\n          <td>\n            <p>\n              <b class="ocpRunInHead">Financeira:</b>&nbsp;&nbsp;&nbsp;&nbsp;retorna o lucro anual de um título que paga juros no vencimento</p>\n          </td>\n        ',
      '\n          <td>\n            <p>\n              <a href="/pt-br/office/teste-z-função-teste-z-d633d5a3-2031-4614-a016-92180ad82bee" class="ocpArticleLink" ms.cmpgrp="content" ms.pgarea="Body">Função TESTE.Z</a>\n              <br>\n<img src="https://support.content.office.net/pt-br/media/f1cec941-a9cf-4f10-8bc9-db8d4fa78f74.png" alt="Excel 2010" ms.cmpgrp="content" ms.pgarea="Body" ms.interactiontype="500">\n</p>\n          </td>\n          <td>\n            <p>\n              <b class="ocpRunInHead">Estatística:</b>&nbsp;&nbsp;&nbsp;&nbsp;retorna o valor de probabilidade unicaudal de um teste z</p>\n          </td>\n        ',
    ],
  }),
  methods: {
    getColor(type) {
      let color = this.colors.filter((item) => {
        if (item.type == type) return item;
      });

      return color[0].color;
    },
    capitalize(str) {
      if (typeof str !== "string") {
        return "";
      }
      return str.charAt(0).toUpperCase() + str.substr(1);
    },
    tratarCores() {},
    trataDados() {
      var dadosTratados = [];
      var dado = {};
      var arrayTypes = [];
      var funcao = "";
      var type = "";
      for (var x = 0; x < this.dados.length; x++) {
        if (this.dados[x]) {
          dado = { funcao: null, link: null, type: null, description: null };
          if (this.dados[x].match(/<a [^>]+>([^<]+)<\/a>/)) {
            funcao = this.dados[x].match(/<a [^>]+>([^<]+)<\/a>/)[1];
            if (funcao.trim() != "") {
              dado.funcao = funcao.split(" ")[1];
            }
          }
          if (this.dados[x].match(/href="([^"]*)/)) {
            dado.link = `https://support.microsoft.com${
              this.dados[x].match(/href="([^"]*)/)[1]
            }`;
          }

          if (this.dados[x].match(/<b [^>]+>([^<]+)<\/b>/)) {
            type = this.dados[x].match(/<b [^>]+>([^<]+)<\/b>/)[1];

            if (type.trim() != "") {
              dado.type = this.capitalize(type.replace(":", ""));
              if (arrayTypes.indexOf(dado.type) === -1) {
                arrayTypes.push(dado.type);
              }
            }
          }

          if (this.dados[x].match(/<\/b>[^>]+;([^<]+)<\/p>/)) {
            dado.description = this.capitalize(
              this.dados[x].match(/<\/b>[^>]+;([^<]+)<\/p>/)[1]
            );
          }

          if (dado.funcao && dado.description) dadosTratados.push(dado);
        }
      }

      this.dadosTratados = dadosTratados;
      this.types = arrayTypes;

      this.getTypeColors;
    },
  },
  mounted() {
    this.trataDados();
  },
};
</script>

